CREATE OR REPLACE FUNCTION public.update_revenue(p_id uuid, p_billing_type text, p_billing_sub_type text, p_from_date date, p_to_date date, p_revenue_amount numeric, p_other_details text) RETURNS void AS $$ BEGIN UPDATE public.revenues SET billing_type = p_billing_type, billing_sub_type = p_billing_sub_type, from_date = p_from_date, to_date = p_to_date, revenue_amount = p_revenue_amount, other_details = p_other_details WHERE id = p_id; END; $$ LANGUAGE plpgsql SECURITY DEFINER;
